bool check_dict_case(map<string, string> dict) {
    // Check if the map is empty
    if (dict.empty()) {
        return false;
    }

    bool all_lower = true;
    bool all_upper = true;

    // Iterate through the map
    for (const auto& pair : dict) {
        const string& key = pair.first;

        // Check if the key is a string
        if (key.empty()) {
            return false;
        }

        // Check if all characters in the key are lower case
        for (char c : key) {
            if (!islower(c)) {
                all_lower = false;
                break;
            }
        }

        // Check if all characters in the key are upper case
        for (char c : key) {
            if (!isupper(c)) {
                all_upper = false;
                break;
            }
        }

        // If neither all lower nor all upper, return false
        if (!all_lower && !all_upper) {
            return false;
        }
    }

    // If all keys are either all lower or all upper, return true
    return true;
}

