string rounded_avg(int n, int m) {
    // Step 1: Check if n is greater than m
    if (n > m) {
        return "-1";
    }

    // Step 2: Calculate the sum of integers from n to m
    int sum = 0;
    for (int i = n; i <= m; ++i) {
        sum += i;
    }

    // Step 3: Calculate the count of integers from n to m
    int count = m - n + 1;

    // Step 4: Calculate the average and round it down
    int average = floor((double)sum / count);

    // Step 5: Convert the rounded average to binary
    string binary = "";
    while (average > 0) {
        binary = to_string(average % 2) + binary;
        average /= 2;
    }

    // If the binary string is empty (which happens if average was 0), set it to "0"
    if (binary.empty()) {
        binary = "0";
    }

    return binary;
}

