    int count = 0;

    for (int num : n) {
        int sum_of_digits = 0;
        int temp = abs(num);

        // Calculate the sum of digits
        while (temp > 0) {
            sum_of_digits += temp % 10;
            temp /= 10;
        }

        // If the number is negative, subtract the sum of digits from 0
        if (num < 0) {
            sum_of_digits = -sum_of_digits;
        }

        // Check if the sum of digits is greater than 0
        if (sum_of_digits > 0) {
            count++;
        }
    }

    return count;
}

