#include <vector>
using namespace std;

bool move_one_ball(vector<int> arr) {
    if (arr.empty()) return true;

    int n = arr.size();
    int dipCount = 0;
    int dipIndex = -1;

    // Find the dip
    for (int i = 0; i < n; ++i) {
        if (arr[i] > arr[(i + 1) % n]) {
            dipCount++;
            dipIndex = i;
        }
    }

    // If there is more than one dip, it's not possible to sort by right shifts
    if (dipCount > 1) return false;

    // If there is exactly one dip, check the segments
    if (dipCount == 1) {
        // Check the segment from dipIndex + 1 to the end
        for (int i = (dipIndex + 1) % n; i != dipIndex; i = (i + 1) % n) {
            if (arr[i] > arr[(i + 1) % n]) {
                return false;
            }
        }
    }

    return true;
}

