map<char, int> histogram(string test) {
    map<char, int> frequencyMap;
    map<char, int> resultMap;
    int maxFrequency = 0;

    // Step 1: Populate the frequency map
    for (char c : test) {
        if (c != ' ') { // Ignore spaces
            frequencyMap[c]++;
            if (frequencyMap[c] > maxFrequency) {
                maxFrequency = frequencyMap[c];
            }
        }
    }

    // Step 2: Populate the result map with letters having the maximum frequency
    for (const auto& pair : frequencyMap) {
        if (pair.second == maxFrequency) {
            resultMap[pair.first] = pair.second;
        }
    }

    return resultMap;
}

