long long minSubArraySum(vector<long long> nums) {
    // Edge case: if the vector is empty, return 0 (or handle as needed)
    if (nums.empty()) return 0;

    // Initialize variables to store the minimum sum and the current sum
    long long minSum = nums[0];
    long long currentSum = nums[0];

    // Iterate through the vector starting from the second element
    for (size_t i = 1; i < nums.size(); ++i) {
        // Update the current sum to be the minimum of the current element or the current sum plus the current element
        currentSum = min(nums[i], currentSum + nums[i]);
        // Update the minimum sum if the current sum is smaller
        minSum = min(minSum, currentSum);
    }

    return minSum;
}

