int max_fill(vector<vector<int>> grid, int capacity) {
    int total_lowers = 0;

    for (const auto& row : grid) {
        int water_units = 0;
        for (int unit : row) {
            if (unit == 1) {
                water_units++;
            }
        }
        // Calculate the number of times the bucket needs to be lowered
        total_lowers += ceil((double)water_units / capacity);
    }

    return total_lowers;
}

