vector<string> select_words(string s, int n) {
    // Define a set of vowels for quick lookup
    unordered_set<char> vowels = {'a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U'};

    // Result vector to store words with exactly n consonants
    vector<string> result;

    // Stringstream to split the input string into words
    stringstream ss(s);
    string word;

    // Iterate through each word in the string
    while (ss >> word) {
        int consonant_count = 0;

        // Count consonants in the current word
        for (char c : word) {
            if (isalpha(c) && vowels.find(c) == vowels.end()) {
                consonant_count++;
            }
        }

        // If the word has exactly n consonants, add it to the result
        if (consonant_count == n) {
            result.push_back(word);
        }
    }

    return result;
