string get_closest_vowel(string word) {
    // Define the set of vowels (case sensitive)
    unordered_set<char> vowels = {'a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U'};

    // Traverse the word from right to left
    for (int i = word.size() - 1; i > 0; --i) {
        // Check if the current character is a vowel
        if (vowels.find(word[i]) != vowels.end()) {
            // Check if it is surrounded by consonants
            bool left_consonant = false, right_consonant = false;

            // Check the character to the left
            if (i > 0 && vowels.find(word[i-1]) == vowels.end()) {
                left_consonant = true;
            }

            // Check the character to the right
            if (i < word.size() - 1 && vowels.find(word[i+1]) == vowels.end()) {
                right_consonant = true;
            }

            // If both conditions are met, return the vowel
            if (left_consonant && right_consonant) {
                return string(1, word[i]);
            }
        }
    }

    // If no such vowel is found, return an empty string
    return "";
}

