bool valid_date(string date) {
    // Check if the date string is empty
    if (date.empty()) {
        return false;
    }

    // Check if the date string is in the format mm-dd-yyyy
    if (date.length() != 10 || date[2] != '-' || date[5] != '-') {
        return false;
    }

    // Extract the month, day, and year from the date string
    string month_str = date.substr(0, 2);
    string day_str = date.substr(3, 2);
    string year_str = date.substr(6, 4);

    // Convert the extracted strings to integers
    int month, day, year;
    try {
        month = stoi(month_str);
        day = stoi(day_str);
        year = stoi(year_str);
    } catch (invalid_argument&) {
        return false;
    }

    // Validate the month
    if (month < 1 || month > 12) {
        return false;
    }

    // Validate the day based on the month
    if (month == 2) {
        // February can have 29 days in a leap year, otherwise 28
        bool is_leap_year = (year % 4 == 0 && (year % 100 != 0 || year % 400 == 0));
        if (day < 1 || (day > 28 && !is_leap_year) || (day > 29 && is_leap_year)) {
            return false;
        }
    } else if (month == 4 || month == 6 || month == 9 || month == 11) {
        // April, June, September, November have 30 days
        if (day < 1 || day > 30) {
            return false;
        }
    } else {
        // January, March, May, July, August, October, December have 31 days
        if (day < 1 || day > 31) {
            return false;
        }
    }

    // If all checks pass, the date is valid
    return true;
}

