vector<string> split_words(string txt) {
    // Check for whitespace
    if (txt.find(' ') != string::npos) {
        vector<string> result;
        stringstream ss(txt);
        string word;
        while (ss >> word) {
            result.push_back(word);
        }
        return result;
    }

    // Check for commas
    if (txt.find(',') != string::npos) {
        vector<string> result;
        stringstream ss(txt);
        string word;
        while (getline(ss, word, ',')) {
            result.push_back(word);
        }
        return result;
    }

    // Count odd-indexed lowercase letters
    int count = 0;
    for (char c : txt) {
        if (islower(c)) {
            int index = c - 'a';
            if (index % 2 != 0) {
                count++;
            }
        }
    }

    return {to_string(count)};
}

