bool is_sorted(vector<int> lst) {
    // Check if the vector is sorted in ascending order
    if (!is_sorted(lst.begin(), lst.end())) {
        return false;
    }

    // Check for duplicates more than twice
    for (size_t i = 0; i < lst.size(); ++i) {
        int count = 0;
        for (size_t j = 0; j < lst.size(); ++j) {
            if (lst[i] == lst[j]) {
                count++;
            }
            if (count > 2) {
                return false;
            }
        }
    }

    return true;
}

