bool isPrime(int n) {
    if (n <= 1) return false;
    if (n <= 3) return true;
    if (n % 2 == 0 || n % 3 == 0) return false;
    for (int i = 5; i * i <= n; i += 6) {
        if (n % i == 0 || n % (i + 2) == 0) return false;
    }
    return true;
}

string intersection(vector<int> interval1, vector<int> interval2) {
    int start1 = interval1[0], end1 = interval1[1];
    int start2 = interval2[0], end2 = interval2[1];

    int start_intersection = max(start1, start2);
    int end_intersection = min(end1, end2);

    if (start_intersection > end_intersection) {
        return "NO";
    }

    int length_of_intersection = end_intersection - start_intersection + 1;

    if (isPrime(length_of_intersection)) {
        return "YES";
    } else {
        return "NO";
    }
}

