#include <vector>
#include <queue>
#include <climits>
using namespace std;

struct Path {
    vector<int> values;
    int x, y;
};

vector<int> minPath(vector<vector<int>> grid, int k) {
    int N = grid.size();
    vector<int> min_path(k, INT_MAX);

    queue<Path> q;

    // Initialize the queue with all starting points
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            q.push({{grid[i][j]}, i, j});
        }
    }

    while (!q.empty()) {
        Path current = q.front();
        q.pop();

        // If the current path length is k, compare and update min_path
        if (current.values.size() == k) {
            if (current.values < min_path) {
                min_path = current.values;
            }
            continue;
        }

        // Possible moves: up, down, left, right
        vector<pair<int, int>> moves = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};

        for (auto move : moves) {
            int newX = current.x + move.first;
            int newY = current.y + move.second;

            if (newX >= 0 && newX < N && newY >= 0 && newY < N) {
                vector<int> newValues = current.values;
                newValues.push_back(grid[newX][newY]);
                q.push({newValues, newX, newY});
            }
        }
    }

    return min_path;
}

