vector<int> tri(int n) {
    vector<int> result(n + 1);

    // Base case
    result[0] = 1; // This is a placeholder for 0-based indexing
    result[1] = 3;

    for (int i = 2; i <= n; ++i) {
        if (i % 2 == 0) {
            // Even case
            result[i] = 1 + i / 2;
        } else {
            // Odd case
            result[i] = result[i - 1] + result[i - 2] + result[i + 1];
        }
    }

    return result;
}

