using namespace std;

boost::any compare_one(boost::any a, boost::any b) {
    // Helper function to convert string to float
    auto string_to_float = [](const string& s) -> float {
        string temp = s;
        boost::replace_all(temp, ",", ".");
        return stof(temp);
    };

    // Helper function to convert any type to float
    auto to_float = [&](const boost::any& value) -> float {
        if (value.type() == typeid(int)) {
            return static_cast<float>(boost::any_cast<int>(value));
        } else if (value.type() == typeid(float)) {
            return boost::any_cast<float>(value);
        } else if (value.type() == typeid(string)) {
            return string_to_float(boost::any_cast<string>(value));
        }
        throw runtime_error("Unsupported type");
    };

    // Convert both values to float for comparison
    float float_a = to_float(a);
    float float_b = to_float(b);

    // Compare the values
    if (float_a > float_b) {
        return a;
    } else if (float_b > float_a) {
        return b;
    } else {
        return string("None");
    }
}

