// Helper function to compute the factorial of a number
long long factorial(int n) {
    long long result = 1;
    for (int i = 1; i <= n; i++) {
        result *= i;
    }
    return result;
}

// Function to compute the Brazilian factorial
long long special_factorial(int n) {
    long long result = 1;
    for (int i = n; i >= 1; i--) {
        result *= factorial(i);
    }
    return result;
}

#include <iostream>
#include <string>
using namespace std;

string fix_spaces(string text) {
    string result = "";
    int n = text.length();
    int i = 0;

    while (i < n) {
        if (text[i] == ' ') {
            int count = 0;
            // Count consecutive spaces
            while (i < n && text[i] == ' ') {
                count++;
                i++;
            }
            // Replace with underscore if only one space
            if (count == 1) {
                result += '_';
            }
            // Replace with hyphen if more than two spaces
            else if (count > 2) {
                result += '-';
            }
            // If exactly two spaces, add two underscores
            else if (count == 2) {
                result += "__";
            }
        } else {
            // Add non-space characters to the result
            result += text[i];
            i++;
        }
    }

    return result;
