string file_name_check(string file_name) {
    // Check if there is exactly one dot
    size_t dot_pos = file_name.find('.');
    if (dot_pos == string::npos || dot_pos == 0 || dot_pos == file_name.size() - 1) {
        return "No";
    }

    // Check if the substring before the dot is not empty and starts with a letter
    string before_dot = file_name.substr(0, dot_pos);
    if (before_dot.empty() || !isalpha(before_dot[0])) {
        return "No";
    }

    // Check if the substring after the dot is one of {"txt", "exe", "dll"}
    string after_dot = file_name.substr(dot_pos + 1);
    if (after_dot != "txt" && after_dot != "exe" && after_dot != "dll") {
        return "No";
    }

    // Count the number of digits in the file name
    int digit_count = 0;
    for (char c : file_name) {
        if (isdigit(c)) {
            digit_count++;
            if (digit_count > 3) {
                return "No";
            }
        }
    }

    // If all conditions are met, return "Yes"
    return "Yes";
}

