int sum_squares(vector<int> lst) {
    // Iterate through the vector
    for (size_t i = 0; i < lst.size(); ++i) {
        // Check if the index is a multiple of 3
        if (i % 3 == 0) {
            lst[i] = lst[i] * lst[i]; // Square the element
        }
        // Check if the index is a multiple of 4 and not a multiple of 3
        else if (i % 4 == 0 && i % 3 != 0) {
            lst[i] = lst[i] * lst[i] * lst[i]; // Cube the element
        }
        // If the index is not a multiple of 3 or 4, do nothing
    }

    // Compute the sum of all elements in the vector
    int sum = 0;
    for (int num : lst) {
        sum += num;
    }

    return sum;
}

