// Helper function to parse the fraction string and return the numerator and denominator
pair<int, int> parseFraction(const string& fraction) {
    size_t pos = fraction.find('/');
    int numerator = stoi(fraction.substr(0, pos));
    int denominator = stoi(fraction.substr(pos + 1));
