// Function to check if the product of two fractions results in a whole number
bool simplify(string x, string n) {
    // Parse the fractions
    pair<int, int> fracX = parseFraction(x);
    pair<int, int> fracN = parseFraction(n);

    // Extract numerators and denominators
    int numX = fracX.first;
    int denX = fracX.second;
    int numN = fracN.first;
    int denN = fracN.second;

    // Calculate the product of the two fractions
    int numerator = numX * numN;
    int denominator = denX * denN;

    // Check if the resulting fraction is a whole number
    return numerator % denominator == 0;
}

