bool right_angle_triangle(float a, float b, float c) {
    // Ensure the sides are in non-decreasing order
    if (a > b) {
        float temp = a;
        a = b;
        b = temp;
    }
    if (b > c) {
        float temp = b;
        b = c;
        c = temp;
    }
    if (a > b) {
        float temp = a;
        a = b;
        b = temp;
    }

    // Now c is the longest side
    return (pow(a, 2) + pow(b, 2) == pow(c, 2));
}

