string solve(string s) {
    bool containsLetter = false;

    // First pass: check if the string contains any letters and reverse case if necessary
    for (int i = 0; i < s.length(); ++i) {
        if (isalpha(s[i])) {
            containsLetter = true;
            if (islower(s[i])) {
                s[i] = toupper(s[i]);
            } else {
                s[i] = tolower(s[i]);
            }
        }
    }

    // If the string contains no letters, reverse the entire string
    if (!containsLetter) {
        string reversed = "";
        for (int i = s.length() - 1; i >= 0; --i) {
            reversed += s[i];
        }
        return reversed;
    }

    return s;
}

