string sort_numbers(string numbers) {
    // Step 1: Create a mapping from number words to their corresponding integer values
    map<string, int> word_to_num = {
        {"zero", 0}, {"one", 1}, {"two", 2}, {"three", 3}, {"four", 4},
        {"five", 5}, {"six", 6}, {"seven", 7}, {"eight", 8}, {"nine", 9}
    };

    // Step 2: Split the input string into individual number words
    vector<string> words;
    stringstream ss(numbers);
    string word;
    while (ss >> word) {
        words.push_back(word);
    }

    // Step 3: Sort the words based on their mapped integer values
    sort(words.begin(), words.end(), [&](const string &a, const string &b) {
        return word_to_num[a] < word_to_num[b];
    });

    // Step 4: Reconstruct the string from the sorted words
    string sorted_numbers;
    for (size_t i = 0; i < words.size(); ++i) {
        if (i != 0) {
            sorted_numbers += " ";
        }
