int fib4(int n) {
    // Base cases
    if (n == 0) return 0;
    if (n == 1) return 0;
    if (n == 2) return 2;
    if (n == 3) return 0;

    // Array to store the last four Fib4 numbers
    int fib4_values[4] = {0, 0, 2, 0};

    // Compute Fib4 numbers iteratively
    for (int i = 4; i <= n; i++) {
        int next_fib4 = fib4_values[0] + fib4_values[1] + fib4_values[2] + fib4_values[3];
        // Shift the values to the left
        fib4_values[0] = fib4_values[1];
        fib4_values[1] = fib4_values[2];
        fib4_values[2] = fib4_values[3];
        fib4_values[3] = next_fib4;
    }

    // The result is the last computed value
    return fib4_values[3];
}

