    while (n % 2 == 0) {
        largest_factor = 2;
        n /= 2;
    }

    // Check for odd factors from 3 to sqrt(n)
    for (int i = 3; i <= sqrt(n); i += 2) {
        while (n % i == 0) {
            largest_factor = i;
            n /= i;
        }
