    stack<char> s;

    for (char bracket : brackets) {
        if (bracket == '(') {
            s.push(bracket);
        } else if (bracket == ')') {
            if (s.empty() || s.top() != '(') {
                return false;
            }
            s.pop();
        }
    }

    return s.empty();
}

