    int n = q.size();
    for (int i = 0; i < n / 2; ++i) {
        if (q[i] != q[n - i - 1]) {
            return false;
        }
    }

    // Calculate the sum of the elements in the vector
    int sum = accumulate(q.begin(), q.end(), 0);

    // Check if the sum is less than or equal to the maximum possible weight
    return sum <= w;
}

