bool iscube(int a) {
    if (a < 0) {
        // If a is negative, we take the absolute value for cube root calculation
        a = -a;
    }
    // Calculate the cube root of a
    double cubeRoot = cbrt(a);
    // Check if the cube root is an integer
    int intCubeRoot = static_cast<int>(cubeRoot);
    // Verify if the cube of the integer cube root equals a
    return (intCubeRoot * intCubeRoot * intCubeRoot == a);
}

