string decimal_to_binary(int decimal) {
    if (decimal == 0) {
        return "db0db";
    }

    string binary;
    while (decimal > 0) {
        binary += (decimal % 2 == 0) ? '0' : '1';
        decimal /= 2;
    }

    // Reverse the string to get the correct binary representation
    reverse(binary.begin(), binary.end());

    // Add the "db" characters at the beginning and end
    return "db" + binary + "db";
}

