string encode(string message) {
    string encodedMessage = "";
    for (char &c : message) {
        if (isalpha(c)) {
            // Swap case
            if (islower(c)) {
                c = toupper(c);
            } else {
                c = tolower(c);
            }

            // Replace vowels with the letter 2 places ahead
            if (c == 'A' || c == 'a') c = 'C';
            else if (c == 'E' || c == 'e') c = 'G';
            else if (c == 'I' || c == 'i') c = 'K';
            else if (c == 'O' || c == 'o') c = 'Q';
            else if (c == 'U' || c == 'u') c = 'W';
            else if (c == 'Y' || c == 'y') c = 'A'; // Assuming 'Y' is a vowel here
        }
        encodedMessage += c;
    }
    return encodedMessage;
}

