#include <iostream>
#include <cmath>
#include <string>
using namespace std;

string rounded_avg(int n, int m) {
    // Step 1: Check if n is greater than m
    if (n > m) {
        return "-1";
    }

    // Step 2: Calculate the sum of integers from n to m
    int sum = (m * (m + 1)) / 2 - ((n - 1) * n) / 2;

    // Step 3: Compute the average
    int count = m - n + 1;
    double average = static_cast<double>(sum) / count;

    // Step 4: Round the average to the nearest integer (smaller one)
    int rounded_average = floor(average);

    // Step 5: Convert the rounded average to binary
    string binary_representation = "";
    while (rounded_average > 0) {
        binary_representation = to_string(rounded_average % 2) + binary_representation;
        rounded_average /= 2;
    }

    // If the binary representation is empty (which happens if rounded_average is 0), return "0"
    return binary_representation.empty() ? "0" : binary_representation;
}

