int count_nums(vector<int> n) {
    int count = 0;

    for (int num : n) {
        int sum_of_digits = 0;
        int temp = abs(num);

        // Calculate the sum of digits
        while (temp > 0) {
            sum_of_digits += temp % 10;
            temp /= 10;
        }

        // If the number is negative, subtract the first digit twice (once for the sign)
        if (num < 0) {
            int first_digit = abs(num) % 10;
            sum_of_digits -= 2 * first_digit;
        }

        // Check if the sum of digits is greater than 0
        if (sum_of_digits > 0) {
            count++;
        }
    }

    return count;
}

