bool move_one_ball(vector<int> arr) {
    int n = arr.size();
    if (n == 0) return true;

    // Find the index of the minimum element
    int minIndex = 0;
    for (int i = 1; i < n; ++i) {
        if (arr[i] < arr[minIndex]) {
            minIndex = i;
        }
    }

    // Check if the elements before minIndex are in decreasing order
    for (int i = 1; i < minIndex; ++i) {
        if (arr[i] < arr[i - 1]) {
            return false;
        }
    }

    // Check if the elements after minIndex are in increasing order
    for (int i = minIndex + 1; i < n; ++i) {
        if (arr[i] < arr[i - 1]) {
            return false;
        }
    }

    // Check the transition from the last element to the first element
    if (minIndex != 0 && arr[n - 1] > arr[0]) {
        return false;
    }

    return true;
}

