    if (nums.empty()) return 0;

    // Initialize variables to store the minimum sum found so far
    long long min_sum = LLONG_MAX;
    long long current_sum = 0;

    // Iterate through the vector
    for (long long num : nums) {
        // Update the current sum to be the minimum of the current element or the current sum plus the element
