    while (n != 1) {
        if (n % 2 == 1) { // Check if n is odd
            odd_numbers.push_back(n);
        }
        if (n % 2 == 0) { // If n is even
            n = n / 2;
        } else { // If n is odd
            n = 3 * n + 1;
        }
    }
