bool isPrime(int n) {
    if (n <= 1) return false;
    if (n == 2) return true; // 2 is the only even prime number
    if (n % 2 == 0) return false; // other even numbers are not prime
    for (int i = 3; i <= sqrt(n); i += 2) {
