#include <vector>
#include <queue>
#include <climits>
using namespace std;

struct State {
    int x, y;
    vector<int> path;
};

vector<int> minPath(vector<vector<int>> grid, int k) {
    int N = grid.size();
    vector<int> min_path(k, INT_MAX);

    // Directions for moving in the grid (up, down, left, right)
    vector<pair<int, int>> directions = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};

    // BFS queue
    queue<State> q;

    // Initialize the queue with all starting positions
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            q.push({i, j, {grid[i][j]}});
        }
    }

    while (!q.empty()) {
        State curr = q.front();
        q.pop();

        // If the current path length is k, compare and update min_path
        if (curr.path.size() == k) {
            if (curr.path < min_path) {
                min_path = curr.path;
            }
            continue;
        }

        // Explore all possible moves
        for (auto& dir : directions) {
            int nx = curr.x + dir.first;
            int ny = curr.y + dir.second;

            // Check if the new position is within the grid
            if (nx >= 0 && nx < N && ny >= 0 && ny < N) {
                vector<int> new_path = curr.path;
                new_path.push_back(grid[nx][ny]);
                q.push({nx, ny, new_path});
            }
        }
    }

    return min_path;
}

