bool is_nested(string str) {
    int open_brackets = 0;
    bool nested = false;

    for (char ch : str) {
        if (ch == '[') {
            open_brackets++;
        } else if (ch == ']') {
            if (open_brackets > 0) {
                open_brackets--;
                nested = true; // Found a nested bracket
            }
        }
    }

    // If there are unmatched open brackets, it means there is no valid subsequence
    if (open_brackets > 0) {
        return false;
    }

    return nested;
}

