boost::any compare_one(boost::any a, boost::any b) {
    // Helper function to convert string to float
    auto string_to_float = [](const string& s) -> float {
        string temp = s;
        boost::replace_all(temp, ",", ".");
        return stof(temp);
    };

    // Helper function to get the value as float
    auto get_value = [&](const boost::any& val) -> float {
        if (val.type() == typeid(int)) {
            return static_cast<float>(boost::any_cast<int>(val));
        } else if (val.type() == typeid(float)) {
            return boost::any_cast<float>(val);
        } else if (val.type() == typeid(string)) {
            return string_to_float(boost::any_cast<string>(val));
        }
        throw runtime_error("Unsupported type");
    };

    // Get the values as floats
    float val_a = get_value(a);
    float val_b = get_value(b);

    // Compare the values
    if (val_a > val_b) {
        return a;
    } else if (val_b > val_a) {
        return b;
    } else {
        return "None";
    }
}

