string fix_spaces(string text) {
    string result;
    int n = text.length();
    int i = 0;

    while (i < n) {
        if (text[i] == ' ') {
            int count = 0;
            // Count consecutive spaces
            while (i < n && text[i] == ' ') {
                count++;
                i++;
            }
            // Replace with underscore if count is 1
            if (count == 1) {
                result += '_';
            }
            // Replace with hyphen if count is more than 2
            else if (count > 2) {
                result += '-';
            }
            // If count is 2, add two underscores
            else if (count == 2) {
                result += "__";
            }
        } else {
            // Add non-space characters to the result
            result += text[i];
            i++;
        }
    }

    return result;
}

