string file_name_check(string file_name) {
    // Check if the file name contains exactly one dot
    size_t dot_pos = file_name.find('.');
    if (dot_pos == string::npos || dot_pos == 0 || dot_pos == file_name.size() - 1) {
        return "No";
    }

    // Check if the substring before the dot is not empty and starts with a latin letter
    string name_part = file_name.substr(0, dot_pos);
    if (name_part.empty() || !isalpha(name_part[0])) {
        return "No";
    }

    // Check if the substring after the dot is one of the allowed extensions
    unordered_set<string> allowed_extensions = {"txt", "exe", "dll"};
    string extension_part = file_name.substr(dot_pos + 1);
    if (allowed_extensions.find(extension_part) == allowed_extensions.end()) {
        return "No";
    }

    // Count the number of digits in the file name
    int digit_count = 0;
    for (char c : file_name) {
        if (isdigit(c)) {
            digit_count++;
            if (digit_count > 3) {
                return "No";
            }
        }
    }

    // If all conditions are met, return "Yes"
    return "Yes";
}

