// Function to split the sentence into words
vector<string> split(const string& sentence) {
    vector<string> words;
    string word;
    for (char ch : sentence) {
        if (ch == ' ') {
            if (!word.empty()) {
                words.push_back(word);
                word.clear();
            }
        } else {
            word += ch;
        }
    }
    if (!word.empty()) {
        words.push_back(word);
    }
    return words;
}

