bool simplify(string x, string n) {
    // Helper function to parse the fraction string
    auto parseFraction = [](const string& fraction) -> pair<int, int> {
        size_t slashPos = fraction.find('/');
        int numerator = stoi(fraction.substr(0, slashPos));
        int denominator = stoi(fraction.substr(slashPos + 1));
        return {numerator, denominator};
    };

    // Parse the fractions
    auto [xNum, xDen] = parseFraction(x);
    auto [nNum, nDen] = parseFraction(n);

