    // Iterate through each pair of numbers
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            // Calculate the absolute difference between numbers[i] and numbers[j]
            float diff = fabs(numbers[i] - numbers[j]);

