float mean_absolute_deviation(vector<float> numbers) {
    // Step 1: Calculate the mean of the dataset
    float sum = accumulate(numbers.begin(), numbers.end(), 0.0);
    float mean = sum / numbers.size();

    // Step 2: Compute the absolute difference between each element and the mean
    float absolute_deviations_sum = 0.0;
    for (float number : numbers) {
        absolute_deviations_sum += abs(number - mean);
    }

    // Step 3: Divide the sum of absolute deviations by the number of elements
    float mad = absolute_deviations_sum / numbers.size();

    return mad;
}

