string sort_numbers(string numbers) {
    // Step 1: Create a mapping from number words to their corresponding integer values
    map<string, int> word_to_num = {
        {"zero", 0}, {"one", 1}, {"two", 2}, {"three", 3}, {"four", 4},
        {"five", 5}, {"six", 6}, {"seven", 7}, {"eight", 8}, {"nine", 9}
    };

    // Step 2: Tokenize the input string into individual number words
    vector<string> words;
    stringstream ss(numbers);
    string word;
    while (ss >> word) {
        words.push_back(word);
    }

    // Step 3: Convert words to numbers using the mapping
    vector<int> nums;
    for (const string& w : words) {
        nums.push_back(word_to_num[w]);
    }

    // Step 4: Sort the list of integers
    sort(nums.begin(), nums.end());

    // Step 5: Convert numbers back to words
    map<int, string> num_to_word = {
        {0, "zero"}, {1, "one"}, {2, "two"}, {3, "three"}, {4, "four"},
        {5, "five"}, {6, "six"}, {7, "seven"}, {8, "eight"}, {9, "nine"}
    };

    vector<string> sorted_words;
    for (int num : nums) {
        sorted_words.push_back(num_to_word[num]);
    }

    // Step 6: Join the sorted words back into a single string with spaces
    string result;
    for (size_t i = 0; i < sorted_words.size(); ++i) {
        result += sorted_words[i];
        if (i < sorted_words.size() - 1) {
            result += " ";
        }
    }

    return result;
}

