    string out;
    int i;
    for (i = 0; i < s.length(); i++) {
        int w = ((int)s[i] + 5 - (int)'a') % 26 + (int)'a';   
        out = out + (char)w;
    }
    return out;
}

string decode_shift(string s) {
    // takes as input string encoded with encode_shift function. Returns decoded string.
    string out;
    int i;
    for (i = 0; i < s.length(); i++) {
        int w = ((int)s[i] - 5 - (int)'a' + 26) % 26 + (int)'a';
        out = out + (char)w;
    }
    return out;
}

