vector<int> pluck(vector<int> arr) {
    // Initialize variables to store the smallest even value and its index
    int min_even_value = -1;
    int min_even_index = -1;

    // Iterate through the vector
    for (int i = 0; i < arr.size(); ++i) {
        // Check if the current element is even
        if (arr[i] % 2 == 0) {
            // If it's the first even value found or it's smaller than the current smallest even value
            if (min_even_value == -1 || arr[i] < min_even_value) {
                min_even_value = arr[i];
                min_even_index = i;
            }
        }
    }

    // If no even value was found, return an empty vector
    if (min_even_value == -1) {
        return {};
    }

    // Return the smallest even value and its index
    return {min_even_value, min_even_index};
}

