// Function to generate all prime numbers less than 100 using Sieve of Eratosthenes
vector<int> generate_primes() {
    const int MAX = 100;
    vector<bool> is_prime(MAX, true);
    is_prime[0] = is_prime[1] = false; // 0 and 1 are not prime numbers

    for (int i = 2; i * i < MAX; ++i) {
        if (is_prime[i]) {
            for (int j = i * i; j < MAX; j += i) {
                is_prime[j] = false;
            }
        }
    }

    vector<int> primes;
    for (int i = 2; i < MAX; ++i) {
        if (is_prime[i]) {
            primes.push_back(i);
        }
    }

    return primes;
}

// Function to check if the given number is the multiplication of 3 prime numbers
bool is_multiply_prime(int a) {
    vector<int> primes = generate_primes();
    int n = primes.size();

    // Check all combinations of three primes
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            for (int k = j + 1; k < n; ++k) {
                if (primes[i] * primes[j] * primes[k] == a) {
                    return true;
                }
            }
        }
    }

    return false;
}

